function parseLocation(e){switch(e){case"All Fires":return"all ";case"#ThomasFire - Ventura, CA 201712":return"thomas ";case"#CreekFire - Los Angeles, CA 201712":return"creek ";case"#RyeFire - Los Angeles, CA 201712":return"rye ";case"#LittleMountainFire - San Bernadino, CA 201712":return"littlemountain ";case"#SkirballFire - Los Angeles, CA 201712":return"skirball ";default:return"other "}}function parseType(e){switch(e){case"Map":return"map";case"News Stories and Unofficial Information":return"news";case"Wind and Weather":return"weather";case"Official Government Site":return"government";case"Traffic and Transportation":return"traffic";case"WebCams":return"webcams";case"Twitter Feeds":return"twitter";case"Mobile App":return"mobile";case"Volunteer Resources":return"volunteer";default:return"other-type"}}function buildCards(e){console.log(e);for(var r=0;r<e.length;r++){var t=document.createElement("li"),a='<a href="'+e[r].url+'" class="'+parseLocation(e[r].fire)+parseType(e[r].type)+'">';a+='<h4 class="name">'+e[r].name+"</h4>",a+='<div class="description">'+e[r].description+"</div>",a+='<div class="fire">Fire: <strong>'+e[r].fire+"</strong></div>",a+='<div class="type">Information type: <strong>'+e[r].type+"</div>",a+="</a>",t.innerHTML=a,MAIN.appendChild(t)}}const DATA="https://docs.google.com/spreadsheets/d/e/2PACX-1vRzGNwiFbhVzNzWQBViXBCYtL5NiU11pgcV3R5KNEza89zPvAWwAdEcSmSO9B-B2TToMT80U-s8cKqy/pub?output=csv",MAIN=document.querySelector(".resource-list ul");Papa.parse(DATA,{download:!0,header:!0,complete:function(e){buildCards(e.data)}});