function clearAll(){if(ALL.checked)for(var e=1;e<LOCATIONFILTERS.length;e++)LOCATIONFILTERS[e].checked&&(LOCATIONFILTERS[e].checked=!1)}function runFilter(e){for(var r=document.querySelectorAll(".resource-list li a"),t=0;t<r.length;t++)if(e.includes("all"))r[t].classList.remove("hide");else{var i=[].slice.apply(r[t].classList);i.includes(e.join())?r[t].classList.remove("hide"):r[t].classList.add("hide")}}function filterSelection(){for(var e=[],r=0;r<FILTERS.length;r++)FILTERS[r].checked&&e.push(FILTERS[r].getAttribute("value"));runFilter(e)}function parseLocation(e){switch(e){case"All Fires":return"all ";case"#ThomasFire - Ventura, CA 201712":return"thomas ";case"#CreekFire - Los Angeles, CA 201712":return"creek ";case"#RyeFire - Los Angeles, CA 201712":return"rye ";case"#LittleMountainFire - San Bernadino, CA 201712":return"littlemountain ";case"#SkirballFire - Los Angeles, CA 201712":return"skirball ";case"#LibertyFire - Murrieta, CA 201712":return"liberty ";case"#LilacFire - San Diego, CA 201712":return"lilac ";default:return"other "}}function parseType(e){switch(e){case"Map":return"map";case"News Stories and Unofficial Information":return"news";case"Wind and Weather":return"weather";case"Official Government Site":return"government";case"Traffic and Transportation":return"traffic";case"WebCams":return"webcams";case"Twitter Feeds":return"twitter";case"Mobile App":return"mobile";case"Volunteer Resources":return"volunteer";default:return"other-type"}}function buildCards(e){for(var r=0;r<e.length;r++){var t=document.createElement("li"),i='<a href="'+e[r].url+'" class="'+parseLocation(e[r].fire)+'">';i+='<h4 class="name">'+e[r].name+"</h4>",i+='<div class="description">'+e[r].description+"</div>",i+='<div class="fire">Fire: <strong>'+e[r].fire+"</strong></div>",i+='<div class="type">Information type: <strong class="'+parseType(e[r].type)+'">'+e[r].type+"</div>",i+="</a>",t.innerHTML=i,MAIN.appendChild(t)}}const DATA="https://docs.google.com/spreadsheets/d/e/2PACX-1vRzGNwiFbhVzNzWQBViXBCYtL5NiU11pgcV3R5KNEza89zPvAWwAdEcSmSO9B-B2TToMT80U-s8cKqy/pub?output=csv",MAIN=document.querySelector(".resource-list ul"),FILTERS=document.querySelectorAll(".filter input"),ALL=document.querySelector("#all"),LOCATIONFILTERS=document.querySelectorAll(".filter.location input");Papa.parse(DATA,{download:!0,header:!0,complete:function(e){buildCards(e.data)}});for(var i=0;i<FILTERS.length;i++)FILTERS[i].addEventListener("change",filterSelection,!1);